let crypto = require("crypto");
function md5(data) {
    return crypto.createHash("md5").update(data).digest("hex");
}
function fixSlash(str) {
    return str
        .replace(/(^|[^:])(\/\/+)/g, "$1/")
        .replace(/\/+$/g, "")
        .replace(/^$/g, "/");
}
module.exports = { md5, fixSlash };
